INSERT INTO "race_jikkyo_message" VALUES(4061,161001,'夢を掴んだスペシャルウィーク！\nついにダービーウマ娘の夢を掴み取りました！','3101001',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4062,161002,'栄光の日曜日の主役となったのはサイレンススズカ！\n第4コーナーの向こう側から\nみごと盾の栄誉を勝ち取りました！','3101002',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4063,161003,'ルドルフが成し遂げたあの無敗の皐月賞制覇！\nトウカイテイオーもその軌跡を辿ってみせました！','3101003',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4064,161004,'先頭マルゼンスキー！\nこの差はちょっと縮まらないかもしれません！\nマルゼンスキーこれは強い！','3101004',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4065,161006,'オグリ1着！　オグリ1着！\n見事にファンの大声援に応えました！\nスーパーウマ娘、オグリキャップです！','3101006',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4066,161007,'ゴールドシップ！\n皐月と菊に続き、グランプリレースでも\n見事金メダルをもぎ取りました！','3101007',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4067,161008,'ウオッカ先頭！　ついにダービーウマ娘の夢かなう！\nウオッカが見事に決めました！','3101008',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4068,161009,'勝ったのはダイワスカーレット！\n夢の扉が今開かれた！　ティアラ路線から\n歩みを進め文句なしのグランプリ制覇です！','3101009',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4069,161010,'タイキシャトル強い！　タイキシャトル文句なし！\n圧勝ーッ！','3101010',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4070,161011,'やっぱり強いウマ娘は強い！\nスペシャルウィークも強かったが、\n勝ったのはグラスワンダーの方だ！','3101011',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4071,161013,'マックイーンだマックイーンだ！\nメジロでもマックイーンの方だー！','3101013',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4072,161014,'エルコンドルパサー！\n府中2400でも強さを見せて\nライバルを蹴散らしました！','3101014',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4073,161017,'大輪の花が薄曇りの京都レース場に大きく咲いた！\n我が国のウマ娘史に名を刻む\nクラシック三冠が達成されました！','3101017',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4074,161018,'エアグルーヴ！　これは恐ろしいウマ娘です！\n女帝が並み居る強豪を蹴散らしました！','3101018',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4075,161024,'勝ったのはマヤノトップガンだ～！\nついにこの菊の舞台で大輪を制しました！','3101024',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4076,161027,'この距離では負けられないメジロライアン！\n宝塚の主役はメジロライアン！\n宝塚の主役はメジロライアンです！','3101027',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4077,161030,'ライスシャワー！　淀の坂を乗り越えて\nみごと夢の宝塚記念を制しました！','3101030',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4078,161032,'中山2000m、まずは道を繋ぎました！\nアグネスタキオンまず一冠！','3101032',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4079,161035,'悲願のダービー制覇！\nウイニングチケット決めました！\n日本ダービーを獲ったウイニングチケットです！','3101035',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4080,161041,'サクラバクシンオーGⅠ獲ったー！\nサクラバクシンオーが右手を挙げました！','3101041',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4081,161045,'オグリキャップは届かない！\nオグリキャップの前に立ちはだかった\nスーパークリークが天皇賞春秋連覇です！','3101045',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4082,161052,'ハルウララがんばれ！　ハルウララがんばれ！\nハルウララの初めての勝利が見えてきた！','3101052',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4083,161056,'京都に続いて神戸も制した！\n神戸にも福が来た！\nマチカネフクキタルが制しました！','3101056',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4084,161060,'ナイスネイチャ、ナイスネイチャです！\nなかなかGⅠを勝てなかったナイスネイチャが、\nこの有馬記念で輝きを放ちました！','3101060',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4085,161061,'キングヘイローがまとめて撫で切った！\n恐ろしい末脚！　ついにGⅠに手が届きました！','3101061',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4086,161015,'テイエムオペラオー！\n盾の舞台に次いでグランプリの舞台でも\n高々に唄うはオペラオーです！','3101015',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4087,161026,'菊近し　仁川の丘越え　ひとり旅\nミホノブルボンといったところであります','2100001',100,0,0);
INSERT INTO "race_jikkyo_message" VALUES(4088,161023,'ビワハヤヒデ宝塚記念圧勝！\n今日も涼しい顔をして\n楽々とゴール板を通過したビワハヤヒデです！','3101023',100,0,0);
