INSERT INTO "text_data" VALUES(144,144,1001,'けっぱるべー！\n夢一直線の純心どさん娘');
INSERT INTO "text_data" VALUES(144,144,1002,'静かなる疾走者。\n音速の先をめざして');
INSERT INTO "text_data" VALUES(144,144,1003,'無敵でキュート！\n天真爛漫ホッピン少女 ');
INSERT INTO "text_data" VALUES(144,144,1004,'昭和レトロな、\nスーパーカーお姉さん');
INSERT INTO "text_data" VALUES(144,144,1005,'エスコートはお任せ！\n華麗なジェントル娘');
INSERT INTO "text_data" VALUES(144,144,1006,'地方生まれの、素朴なクールドリーマー');
INSERT INTO "text_data" VALUES(144,144,1007,'粗暴、奔放！　\n謎テンションな黄金電波娘');
INSERT INTO "text_data" VALUES(144,144,1008,'突っ走る青春！\n純情不良なロードスター');
INSERT INTO "text_data" VALUES(144,144,1009,'唯我独尊！\n負けん気満点のツンツンガール');
INSERT INTO "text_data" VALUES(144,144,1010,'アメリカ生まれのフレンドリーガール');
INSERT INTO "text_data" VALUES(144,144,1011,'清楚可憐。\nアメリカ帰りの大和撫子');
INSERT INTO "text_data" VALUES(144,144,1012,'タイマン上等！\n頼れる我らの姐御寮長');
INSERT INTO "text_data" VALUES(144,144,1013,'名門メジロ家の至宝。\n優雅で一途なお嬢様');
INSERT INTO "text_data" VALUES(144,144,1014,'北米の怪鳥！\n快活奇抜なルチャリブレ');
INSERT INTO "text_data" VALUES(144,144,1015,'尊大なる歌劇王。\nその哄笑は高らかに！');
INSERT INTO "text_data" VALUES(144,144,1017,'君臨する“皇帝”\nその権威に揺らぎなし！	');
INSERT INTO "text_data" VALUES(144,144,1018,'冷徹なる“女帝”\n才色兼備のキャリアレディ');
INSERT INTO "text_data" VALUES(144,144,1019,'ウマ娘ちゃん大好き！\nスーパー万能オタク娘');
INSERT INTO "text_data" VALUES(144,144,1020,'マイペース、マイライフ。\n漂う雲のように…');
INSERT INTO "text_data" VALUES(144,144,1021,'ツッコミは稲妻の如し\n見せろ根性、浪花っ娘');
INSERT INTO "text_data" VALUES(144,144,1022,'自由奔放思い出作り！\n箱入りロイヤルレディ');
INSERT INTO "text_data" VALUES(144,144,1023,'情熱的知性派。論理を\n武器に立ち向かえ！');
INSERT INTO "text_data" VALUES(144,144,1024,'甘々な末っ子ガール。\n早く大人になりたい！');
INSERT INTO "text_data" VALUES(144,144,1025,'見えない影を追い走る\nミステリアス少女');
INSERT INTO "text_data" VALUES(144,144,1026,'指示は絶対厳守！\nサイボーグ・ウマ娘');
INSERT INTO "text_data" VALUES(144,144,1027,'気分は爽快スポーティ！\n輝く汗も純粋に');
INSERT INTO "text_data" VALUES(144,144,1029,'目指せシチーガール！\n地方出身の頑張り屋');
INSERT INTO "text_data" VALUES(144,144,1030,'不幸体質？\n健気に生きる薄幸乙女');
INSERT INTO "text_data" VALUES(144,144,1031,'元気いーっぱい！\n家族思いの働き者');
INSERT INTO "text_data" VALUES(144,144,1032,'禁断のマッド科学者。\n肉体研究こそすべて！');
INSERT INTO "text_data" VALUES(144,144,1035,'思い切り笑って泣いて！\n感情全開の正直娘');
INSERT INTO "text_data" VALUES(144,144,1036,'ロジカルにいこうぜ！\n常識外れの天才ガール');
INSERT INTO "text_data" VALUES(144,144,1037,'すべては計画通りに。\n謹厳実直なメッチェン');
INSERT INTO "text_data" VALUES(144,144,1040,'ぶっきらぼうなハイクオリティ・ギャル');
INSERT INTO "text_data" VALUES(144,144,1041,'大暴走・学級委員長。\nいつだって爆進！');
INSERT INTO "text_data" VALUES(144,144,1044,'ピュアにダダっ子！\n絶対魔女になるもん！');
INSERT INTO "text_data" VALUES(144,144,1045,'おっとり母性で癒してくれる優しいお姉さん');
INSERT INTO "text_data" VALUES(144,144,1046,'ファンサなら任せて！\n未来のトップウマドル');
INSERT INTO "text_data" VALUES(144,144,1050,'クール＆ドライ！\n小柄な体で勝ちをとる');
INSERT INTO "text_data" VALUES(144,144,1051,'花咲く笑顔♪\n気配り上手なウマ娘');
INSERT INTO "text_data" VALUES(144,144,1052,'連敗しても、くじけない！\n七転八起ガール');
INSERT INTO "text_data" VALUES(144,144,1054,'正義の炎が燃え盛る！\n熱きヒーロー、見参！');
INSERT INTO "text_data" VALUES(144,144,1055,'マーベラース！\n元気に無敵に世界を変える！');
INSERT INTO "text_data" VALUES(144,144,1056,'福よ来い！\n占い大好きなハッピー祈願娘');
INSERT INTO "text_data" VALUES(144,144,1058,'前向きになりたい！\nネガティブな頑張り屋');
INSERT INTO "text_data" VALUES(144,144,1059,'変わりたい！過去の\n傷を抱えて走るお嬢様');
INSERT INTO "text_data" VALUES(144,144,1060,'期待はノーサンキュー？な\nシニカルガール');
INSERT INTO "text_data" VALUES(144,144,1061,'高飛車お嬢様。\nそのプライドは空より高く！');
INSERT INTO "text_data" VALUES(144,144,1063,'管理徹底主義。\n冷熱併せた敏腕ウマ娘');
INSERT INTO "text_data" VALUES(144,144,1064,'気楽にいこーよ。\n頼れるポジティブギャル');
INSERT INTO "text_data" VALUES(144,144,1065,'盛ってウケて神アゲ☆\nエヴリデイ・パリピ！');
INSERT INTO "text_data" VALUES(144,144,1066,'ターボ全開！\n限界知らずの爆走ウマ娘！');
INSERT INTO "text_data" VALUES(144,144,9001,'笑顔が素敵なトレセン\n学園理事長秘書さん');
INSERT INTO "text_data" VALUES(144,144,9004,'直向きにどこまでも！\n意欲溢れるトレーナー');
