INSERT INTO "text_data" VALUES(27,27,8,'十分な実力があるので、\n<color=#FF6D26>【有力なウマ娘の1人】</color>になるでしょう。');
INSERT INTO "text_data" VALUES(27,27,9,'気合いが程よく乗ってて良い雰囲気ですね。\n<color=#FF6D26>【1着争い】</color>への参戦も可能でしょう。');
INSERT INTO "text_data" VALUES(27,27,10,'気合いが程よく乗ってて良い雰囲気ですね。\n<color=#FF6D26>【有力なウマ娘の1人】</color>に挙がるでしょう。');
INSERT INTO "text_data" VALUES(27,27,11,'今の状態なら不安なくレースに臨めそうです。\n<color=#FF6D26>【有力なウマ娘の1人】</color>になるかもしれません。');
